package com.example.bookingparking.admin.dashboardimport android.annotation.SuppressLintimport android.content.Intentimport android.util.Logimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.bookingparking.databinding.ItemViewDashboardBindingimport com.example.bookingparking.model.ParkingTicketimport com.google.firebase.database.ktx.databaseimport com.google.firebase.ktx.Firebaseclass DashboardAdapter :    RecyclerView.Adapter<DashboardAdapter.DashboardViewHolder>() {    private val reference = Firebase.database.reference    private val listData = ArrayList<ParkingTicket>()    @SuppressLint("NotifyDataSetChanged")    fun addData(data: ArrayList<ParkingTicket>){        Log.d("BEFORE", data.toString())        listData.clear()        Log.d("AFTER", data.toString())        listData.addAll(data)        notifyDataSetChanged()    }    inner class DashboardViewHolder(private val binding: ItemViewDashboardBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(parkingTicket: ParkingTicket) {            binding.apply {                labelName.text = parkingTicket.name                labelCodeParking.text = parkingTicket.codeParking                labelNumberPlate.text = parkingTicket.plate            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): DashboardViewHolder {        return DashboardViewHolder(ItemViewDashboardBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: DashboardViewHolder, position: Int) {        holder.bind(listData[position])        holder.itemView.setOnClickListener {            val sendData = listData[position]            val intent = Intent(holder.itemView.context, DetailParkingConfirmationActivity::class.java)            intent.putExtra(DetailParkingConfirmationActivity.EXTRA_NAME, sendData.name)            intent.putExtra(DetailParkingConfirmationActivity.EXTRA_PLATE_NUMBER, sendData.plate)            intent.putExtra(DetailParkingConfirmationActivity.EXTRA_CODE_PARKING, sendData.codeParking)            intent.putExtra(DetailParkingConfirmationActivity.EXTRA_UID, sendData.saveUID)            intent.putExtra(DetailParkingConfirmationActivity.EXTRA_DOC_ID, sendData.docTicketID)            holder.itemView.context.startActivity(intent)        }    }    override fun getItemCount(): Int {        return listData.size    }}