package com.example.bookingparking.helperimport android.content.Contextclass SharedPreference(context: Context) {    companion object {        private const val PREF_NAME = "com.example.bookingparking.shared.preference"        private const val ACCOUNT_TYPE = "account_type"        private const val USER_ID = "user_id"        private const val LOGIN_STATE = "login_state"        private const val DOC_ID = "doc_id"    }    private val preference = context.getSharedPreferences(PREF_NAME, Context.MODE_PRIVATE)    private val editor = preference.edit()    fun saveLoginState(isLogin: Boolean) {        editor.putBoolean(LOGIN_STATE, isLogin)        editor.apply()    }    fun saveAccountType(type: String) {        editor.putString(ACCOUNT_TYPE, type)        editor.apply()    }    fun saveUserID(userID: String) {        editor.putString(USER_ID, userID)        editor.apply()    }    fun saveDocumentIdFirestore(docID: String) {        editor.putString(DOC_ID, docID)        editor.apply()    }    /**     * #######################################################     */    fun getLoginState(): Boolean {        return preference.getBoolean(LOGIN_STATE, false)    }    fun getAccountType(): String {        return preference.getString(ACCOUNT_TYPE, "").toString()    }    fun getDocID(): String {        return preference.getString(DOC_ID, "").toString()    }    fun getUserID(): String {        return preference.getString(USER_ID, "").toString()    }    fun clear() {        editor.remove(LOGIN_STATE)        editor.remove(ACCOUNT_TYPE)        editor.remove(USER_ID)        editor.remove(DOC_ID)        editor.apply()    }}